INSERT INTO au_article VALUES ('180', '37', 'Autenticidad en el corazón de tu experiencia', '', '- Una experiencia auténtica cerca a la cultura y los locales.<br />\r\n- Actividades Interesantes y Amables (Prueba las comidas tradiciones, disfruta de excursiones en motos, bicicletas o caminatas,visita de los pueblos hermosos, etc.)', '', '', null, '1', '5', '', '', '', '5', '5', null, null, '0', '0', '0', '2018-07-20 10:04:17', '2018-04-04 03:40:38');
