INSERT INTO au_tour VALUES ('100', '14', 'Hanoi - Saigon Package Tour', 'hanoi-saigon-package-tour', 'Hanoi - Halong - Saigon - Mekong ', '<ul>\r\n	<li>Walk in the old quarter of Hanoi.</li>\r\n	<li>Discover the highlights of a historic thousand-year-old city.</li>\r\n	<li>Stay overnight and discover the amazing landscape of Halong Bay.</li>\r\n	<li>The Reunification palace (or the war memorials Museum), the Notre Dame Cathedral and the Central Post Office in Saigon</li>\r\n	<li>Discover the Mekong River Delta with its floating market and traditional handicraft villages.<br />\r\n	&nbsp;</li>\r\n</ul>\r\n', '', '7', '<ul>\r\n	<li>Accommodation with daily breakfast&nbsp;</li>\r\n	<li>Private car transfer&nbsp;</li>\r\n	<li>Halong Bay with Cruise&rsquo;s Shuttle bus ( Private car &nbsp;is available on request)</li>\r\n	<li>Private cabin ( 2 persons/each cabin in cruise)</li>\r\n	<li>All entrance fees</li>\r\n	<li>English speaking guides</li>\r\n	<li>Meals as indicated</li>\r\n	<li>Domestic flights: Hanoi to Saigon</li>\r\n	<li>Vietnam Visa approval letter.</li>\r\n	<li>Tax and service charges</li>\r\n</ul>\r\n', '<ul>\r\n	<li>International flight tickets from/to your country</li>\r\n	<li>Travel insurance</li>\r\n	<li>Meals not indicated&nbsp;</li>\r\n	<li>Personal Expenses : laundry, drinking, telephone&hellip;</li>\r\n	<li>Tipping and gratuities&nbsp;<br />\r\n	&nbsp;</li>\r\n</ul>\r\n', '', '1', null, '0', '61421910b3a2efbd04b9715677800991.jpg', '', '', '', '1', '232', '11,12,35,38', '', '', '', '', '', '2018-06-04 03:42:50', '2018-05-10 01:29:41', '5', '5', 'US$ 560', '1135', '715', '650', '560', 'Contact us', '190');
