INSERT INTO au_tourdetail VALUES ('326', '70', '1', 'Siem Reap arrival (-/-/D)', '<p style=\"text-align:justify\">Upon arrival, our Authentik Travel tour guide will welcome you at the charming<strong> Siem Reap International Airport</strong> and transfer you a short distance to the down town area.<br />\r\nCheck in at your hotel and enjoy a <strong>briefing tour</strong> as well as some first information about this beautiful region.<br />\r\n<strong>A welcome dinner</strong> with an <strong>Apsara Dancing show</strong> will be served in the finest restaurant in the city.<br />\r\nStay overnight in Siem Reap.</p>\r\n', null, '13779a2bb9304195e29c509cd83aea66.jpg', '2018-05-29 07:27:11', '2017-11-14 20:43:06', '5', '5');
