INSERT INTO au_tourdetail VALUES ('507', '88', '10', 'Ngapali beach – fly to Yangon (B/-/-)', '<p style=\"text-align:justify\">Free till <strong>transfer to Thandwe airport for flight back to Yangon.</strong><br />\r\nOn arrival, meet our driver for <strong>transfer to hotel</strong> in the city center.<br />\r\nOvernight in Yangon.</p>\r\n', null, 'a2fcdcf3600fc88e9d0175536ca2534e.jpg', '2018-05-16 08:20:20', '2017-11-12 20:29:39', '5', '5');
